from had.shourtcuts import get_url
from had.shourtcuts import error_render

def get_url_by_slug(app_name, username, slug):
  # app_nameはdetail等
  if "/" not in slug:
    return get_url(app_name+"1", username=username, slug1=slug)
  slug_list = slug.split("/")
  if slug_list[0] == "":
    raise Exception("slug is empty")
  if slug_list[-1] == "":
    slug_list.pop()
  length = len(slug_list)
  kwargs = {"username": username}
  for i, s in enumerate(slug_list):
    kwargs["slug"+str(i+1)] = s
  return get_url(app_name+str(length), **kwargs)

