from wtforms import Form, BooleanField, StringField, PasswordField, validators, IntegerField, TextAreaField
from wtforms.validators import DataRequired, Length

class PageForm(Form):
  slug = StringField(
    render_kw={'style': 'width: 500px;'},
    validators=[DataRequired(), Length(min=1, max=127)]
  )
  priority = IntegerField(validators=[DataRequired()])
  public = BooleanField()
  edit_permission = BooleanField()
  title = StringField(
    render_kw={'style': 'width: 100%; height: auto; font-size: 2rem;'},
    validators=[DataRequired(), Length(min=1, max=127)]
  )
  text = TextAreaField()
  share = BooleanField()
  share_code = StringField(render_kw={'style': 'width: 500px;'})
  share_edit_permission = BooleanField()
